<?php

use Ganlv\Z5EncryptDecompiler\Disassembler1;
use Ganlv\Z5EncryptDecompiler\Disassembler2;
use Ganlv\Z5EncryptDecompiler\Disassembler3;
use Ganlv\Z5EncryptDecompiler\PhpParserHelper;

require 'vendor/autoload.php';

$data = gzinflate(base64_decode(""));
$text = gzinflate(base64_decode(""));

$disassembler1 = new Disassembler1($data, $text);
$disassembler1->disassemble();

$disassembler2 = new Disassembler2($disassembler1);
$disassembler2->disassemble();

$file = file_get_contents('tests/samples/t2.php_');
$disassembler3 = new Disassembler3($disassembler2, $file, 'func');
$text2 = $disassembler3->disassemble();

$disassembler1_2 = new Disassembler1($data, $text2);
$disassembler1_2->disassemble();

$disassembler2_2 = new Disassembler2($disassembler1_2);
$disassembler2_2->disassemble();

file_put_contents('tests/samples/t2_disassembled.php_', PhpParserHelper::prettyPrintFile(PhpParserHelper::beautify($disassembler2_2->ast)));
echo PhpParserHelper::prettyPrintFile(PhpParserHelper::beautify($disassembler2_2->ast)), PHP_EOL;
